import sequelize from "@/config/db";
import { createProvider, updateProvider } from "@/utils/api/ProviderLibs";
import { ProviderUpdatePayload } from "@/interfaces/ProviderInterfaces";
import { ProviderSchema } from "@/schema/ProviderSchema";
import {
  PRIMARY_KEY,
  UNIQUE_FIELDS,
} from "@/utils/constants/ProviderConstants";
import handleSequelizeError from "@/utils/errorHandling";
import { checkDuplicateCombinations, returnJSONResponse } from "@/utils/utils";
import { NextResponse } from "next/server";

//Generated by GetMultiCreateModelPOSTRoute - GetMultiCreateModelPOSTRoute
export const POST = async (req: Request) => {
  const body = (await req.json()) as ProviderUpdatePayload;
  const { Providers } = body;

  //Generated by GenerateUniquenessValidation
  //Validate record uniqueness

  UNIQUE_FIELDS.forEach((item) => {
    const uniquenessError = checkDuplicateCombinations(item, Providers);

    if (uniquenessError) {
      return returnJSONResponse({
        status: "error",
        error: uniquenessError,
        errorCode: 422,
      });
    }
  });

  const t = await sequelize.transaction();
  let recordsCreated = 0;

  for (const item of Providers) {
    try {
      await ProviderSchema.validate(item);
    } catch (error) {
      return handleSequelizeError(error);
    }
  }

  try {
    for (const item of Providers) {
      if (item[PRIMARY_KEY] === "") {
        await createProvider(item, t);
        recordsCreated++;
      } else {
        await updateProvider(item, PRIMARY_KEY, t);
      }
    }

    await t.commit();

    return NextResponse.json({
      recordsCreated,
    });
  } catch (err) {
    await t.rollback();
    return handleSequelizeError(err);
  }
};
