"use client";
//Generated by GeneratePageFile
import React, { useState } from "react";
import { cn } from "@/lib/utils";
import { Icons } from "@/components/Icons";
import { useTheme } from "next-themes";

const DarkLightToggle: React.FC = () => {
  const { theme, setTheme } = useTheme();
  const darkMode = !theme || theme === "dark";
  const toggleTheme = () => {
    const changeToTheme = darkMode ? "light" : "dark";
    setTheme(changeToTheme);
  };

  return (
    <div
      className={cn(
        "border-[2.5px] border-green-900 rounded-[30px] flex justify-between items-center p-1 relative gap-2 cursor-pointer text-sm select-none"
      )}
      onClick={toggleTheme}
    >
      <div>
        <Icons.lightMode className="w-4 h-4" />
      </div>
      <div>
        <Icons.darkMode className="w-4 h-4" />
      </div>
      <div
        className={`absolute h-4 w-4 bg-green-700 rounded-full transition-all ease-in-out duration-150 ${
          !darkMode ? "left-1" : "left-7"
        }`}
      ></div>
    </div>
  );
};

export default DarkLightToggle;
