"use client";
import { GetEmployerOfficesResponse } from "@/interfaces/EmployerOfficeInterfaces";
import { EmployerOfficeSearchParams } from "@/interfaces/EmployerOfficeInterfaces";
import { BasicModel } from "@/interfaces/GeneralInterfaces";
import axiosClient from "@/utils/api";
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";

const getEmployerOffices = async (useName: boolean = false) => {
  const { data } = await axiosClient.get<GetEmployerOfficesResponse>(`employer-offices`, {
    params: {
      fetchCount: "false",
      simpleOnly: "true",
    } as Partial<EmployerOfficeSearchParams>,
  });

  return data.rows.map((item) => ({
    id: !useName ? item.id : item.name,
    name: item.name,
  }));
};

interface UseListProps {
  placeholderData?: BasicModel[];
  useName?: boolean;
}

const useEmployerOfficeList = (prop?: UseListProps) => {
  //local states
  const [mounted, setMounted] = useState(false);

  const _ = useQuery({
    queryKey: ["employerOffice-list"],
    queryFn: () => getEmployerOffices(prop?.useName),
    enabled: mounted,
    placeholderData: prop?.placeholderData,
  });

  useEffect(() => {
    setMounted(true);
  }, []);

  return _;
};

export default useEmployerOfficeList;
