"use client";
import { GetPositionsResponse } from "@/interfaces/PositionInterfaces";
import { PositionSearchParams } from "@/interfaces/PositionInterfaces";
import { BasicModel } from "@/interfaces/GeneralInterfaces";
import axiosClient from "@/utils/api";
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";

const getPositions = async () => {
  const { data } = await axiosClient.get<GetPositionsResponse>(`positions`, {
    params: {
      fetchCount: "false",
      simpleOnly: "true",
    } as Partial<PositionSearchParams>,
  });

  return data.rows.map((item) => ({
    id: item.id,
    name: item.name,
  }));
};

interface UseListProps {
  placeholderData?: BasicModel[];
}

const usePositionList = (prop?: UseListProps) => {
  //local states
  const [mounted, setMounted] = useState(false);

  const _ = useQuery({
    queryKey: ["position-list"],
    queryFn: getPositions,
    enabled: mounted,
    placeholderData: prop?.placeholderData,
  });

  useEffect(() => {
    setMounted(true);
  }, []);

  return _;
};

export default usePositionList;
