//Generated by GetuseModelListts - useModelList.ts
"use client";
import { GetProvidersResponse } from "@/interfaces/ProviderInterfaces";
import { ProviderSearchParams } from "@/interfaces/ProviderInterfaces";
import { BasicModel } from "@/interfaces/GeneralInterfaces";
import axiosClient from "@/utils/api";
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";

const getProviders = async (useName: boolean = false) => {
  const { data } = await axiosClient.get<GetProvidersResponse>(`providers`, {
    params: {
      fetchCount: "false",
      simpleOnly: "true",
    } as Partial<ProviderSearchParams>,
  });

  return data.rows.map((item) => ({
    id: !useName ? item.id : item.name,
    name: item.name,
  }));
};

interface UseListProps {
  placeholderData?: BasicModel[];
  useName?: boolean;
}

const useProviderList = (prop?: UseListProps) => {
  //local states
  const [mounted, setMounted] = useState(false);

  const _ = useQuery({
    queryKey: ["provider-list"],
    queryFn: () => getProviders(prop?.useName),
    enabled: mounted,
    placeholderData: prop?.placeholderData,
  });

  useEffect(() => {
    setMounted(true);
  }, []);

  return _;
};

export default useProviderList;
