"use client";
import { GetStatusResponse } from "@/interfaces/StatusInterfaces";
import { StatusSearchParams } from "@/interfaces/StatusInterfaces";
import { BasicModel } from "@/interfaces/GeneralInterfaces";
import axiosClient from "@/utils/api";
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";

const getStatus = async (useName: boolean = false) => {
  const { data } = await axiosClient.get<GetStatusResponse>(`statuses`, {
    params: {
      fetchCount: "false",
      simpleOnly: "true",
    } as Partial<StatusSearchParams>,
  });

  return data.rows.map((item) => ({
    id: !useName ? item.id : item.name,
    name: item.name,
  }));
};

interface UseListProps {
  placeholderData?: BasicModel[];
  useName?: boolean;
}

const useStatusList = (prop?: UseListProps) => {
  //local states
  const [mounted, setMounted] = useState(false);

  const _ = useQuery({
    queryKey: ["status-list"],
    queryFn: () => getStatus(prop?.useName),
    enabled: mounted,
    placeholderData: prop?.placeholderData,
  });

  useEffect(() => {
    setMounted(true);
  }, []);

  return _;
};

export default useStatusList;
