"use client";
import { GetTrackingStatusResponse } from "@/interfaces/TrackingStatusInterfaces";
import { TrackingStatusSearchParams } from "@/interfaces/TrackingStatusInterfaces";
import { BasicModel } from "@/interfaces/GeneralInterfaces";
import axiosClient from "@/utils/api";
import { useQuery } from "@tanstack/react-query";
import { useEffect, useState } from "react";

const getTrackingStatus = async () => {
  const { data } = await axiosClient.get<GetTrackingStatusResponse>(`tracking-status`, {
    params: {
      fetchCount: "false",
      simpleOnly: "true",
    } as Partial<TrackingStatusSearchParams>,
  });

  return data.rows.map((item) => ({
    id: item.id,
    name: item.name,
  }));
};

interface UseListProps {
  placeholderData?: BasicModel[];
}

const useTrackingStatusList = (prop?: UseListProps) => {
  //local states
  const [mounted, setMounted] = useState(false);

  const _ = useQuery({
    queryKey: ["trackingStatus-list"],
    queryFn: getTrackingStatus,
    enabled: mounted,
    placeholderData: prop?.placeholderData,
  });

  useEffect(() => {
    setMounted(true);
  }, []);

  return _;
};

export default useTrackingStatusList;
